const searchInput=document.querySelector(".search-input"),allAccountss=document.querySelectorAll(".accounts .account"),accountsName=Array.from(document.querySelectorAll("h3.account__name")),accountsType=Array.from(document.querySelectorAll("h3.account__type")),accountsCurrency=Array.from(document.querySelectorAll(".accounts .currency"));function filter(e,c){return e.filter((e=>e.innerText.toLowerCase().includes(c)))}function displayFilteredEl(e){for(const c of e){c.closest(".account").style.display=""}}searchInput.addEventListener("keyup",(()=>{const e=searchInput.value.toLowerCase(),c=filter(accountsName,e),t=filter(accountsType,e),r=filter(accountsCurrency,e);allAccountss.forEach((e=>e.style.display="none")),displayFilteredEl(c),displayFilteredEl(t),displayFilteredEl(r)}));