const userNameInput=document.querySelector(".settings__form .username__input"),userNameVal=localStorage.getItem("username"),baseCurrencySelect=document.querySelector(".base__currency-select"),baseCurrencyVal=localStorage.getItem("base__currency");userNameInput.value=userNameVal,fetch("../static/data/countriesInfo.json").then((e=>e.json())).then((e=>{e.forEach((e=>{const r=e.countryName,t=e.currencyName,a=e.currencyCode,n=`<option ${a===baseCurrencyVal?"selected":""} value="${a}" data-country-name=${r}>${a} - ${t}</option>`;baseCurrencySelect.insertAdjacentHTML("beforeend",n)}))}));