const userNameInput=document.querySelector(".settings__form .username__input"),userNameVal=localStorage.getItem("username"),baseCurrencySelect=document.querySelector(".base__currency-select"),baseCurrencyVal=localStorage.getItem("base__currency"),changeBtns=document.querySelectorAll(".settings__form .change");userNameInput.value=userNameVal,fetch("../static/data/countriesInfo.json").then((e=>e.json())).then((e=>{e.forEach((e=>{const t=e.countryName,n=e.currencyName,r=e.currencyCode,c=`<option ${r===baseCurrencyVal?"selected":""} value="${r}" data-country-name=${t}>${r} - ${n}</option>`;baseCurrencySelect.insertAdjacentHTML("beforeend",c)}))})),changeBtns.forEach((e=>{e.addEventListener("click",(e=>{e.preventDefault();const t=e.target.previousElementSibling;t.classList.remove("disabled"),t.focus()}))}));