const currenciesList=document.querySelector(".currencies__list"),confirmStepBtns=document.querySelectorAll(".confirm__step"),setupForm=document.querySelectorAll(".setup__form");let stepsCount=0,barWidth=50;const selectedCurrency=document.querySelector(".selected__currency"),cashBalance=document.querySelector(".cash__balance .input"),confirmCashBtn=document.querySelector(".setup__form .cash");confirmStepBtns.forEach((e=>{e.addEventListener("click",(t=>{setupForm[stepsCount].checkValidity()&&t.preventDefault();const s=e.getAttribute("data-step-name");if(document.querySelector(`.${s} .input`).value){const e=document.querySelector(`.${s}`);if(e.classList.remove("active"),e.classList.add("hide"),e.nextElementSibling){const t=e.nextElementSibling;t.classList.remove("hide"),t.classList.add("active")}const t=document.querySelectorAll(".setup__header .step");if(stepsCount<t.length){if(t[stepsCount].classList.contains("step__active")){t[stepsCount].classList.remove("step__active"),t[stepsCount].classList.add("step__done");if(document.querySelector(".bar__done").style.cssText=`background-color: #5ec576; width: ${barWidth}%`,t[stepsCount].nextElementSibling){t[stepsCount].nextElementSibling.classList.add("step__active")}}stepsCount++,barWidth<100&&(barWidth*=2)}}}))}));